window.onscroll=function showHeader(){var a=document.querySelector(".header");if(window.pageYOffset>150){a.classList.add("header_fixed")}else{a.classList.remove("header_fixed")}var a=document.querySelector(".optimization");if(window.pageYOffset>150){a.classList.add("optimization_fixed")}else{a.classList.remove("optimization_fixed")}var a=document.querySelector(".header-top");if(window.pageYOffset>150){a.classList.add("header-top_fixed")}else{a.classList.remove("header-top_fixed")}};